import { Grid } from "gridjs";
import {onMount} from 'svelte'

onMount(createGrid)

let element
let grid

$: columns = table_headings.map(h => h.heading)
$: data = table_rows.map(r => r.columns.map(c => c.text)) 
$: element && updateGrid({columns, data, config})

function createGrid() {
  grid = new Grid({ 
    columns, 
    data,
    sort: config.sortable,
    height: `${config.height*100}px`, 
    resizable: true,
  }).render(element)
}

function updateGrid({columns, data, config}) {
  grid.updateConfig({
    columns,
    data,
    sort: config.sortable,
    height: `${config.height*100}px`
  })
  grid.forceRender()
}

