import { MasonryGrid } from "@egjs/grid";
import { onMount } from "svelte";

const gapMultiplier = 10

let container;
let grid;

$: gap = parseInt(design.gap) * gapMultiplier;
$: columns = (window.innerWidth < design.breakpoint) ? 0 : parseInt(design.columns) 

$: container && renderGrid();

function renderGrid() { 
  if (grid) grid.destroy();
  grid = new MasonryGrid(container);
  grid.renderItems();
}

$: grid && updateDesign({ gap, columns })
function updateDesign({columns, gap}) { 
  renderGrid()
  grid.column = columns
  grid.gap = gap 
}

