import Icon from "@iconify/svelte/dist/Icon.svelte";

let links = []
function createHeaderLinks(content) {
  var parser = new DOMParser();
  var htmlDoc = parser.parseFromString(content, 'text/html');
  const body = htmlDoc.querySelector('body')
  links = Array.from(body.children).filter(n => ['H1', 'H2', 'H3', 'H4'].includes(n.tagName)).map(n => {
    return ({
      level: n.tagName,
      text: n.textContent,
      id: n.id,
      node: n,
      top: 0
    })
  })
}
$: createHeaderLinks(content)

let activeLink = null
let scrollY
$: setActiveLink(scrollY) 
function setActiveLink(scrollY) {
  links = links.map((link, i) => {
    const element = document.querySelector(`#${link.id}`) 
    const top = element ? element.offsetTop : null
    const passed = scrollY >= top
    return { 
      ...link,
      top,
      passed
    }
  }).map((link, i) => {
    const nextLink = links[i+1]
    const active = nextLink ? (link.top < scrollY && scrollY < nextLink.top) : false
    return {
      ...link,
      active
    }
  })
}

